pragma solidity ^0.5.10;

import "../permissions/AuthChecker.sol";

/**
 * @notice ACLGuard is a Guard implementation that checks canCall permissions against an Access Control List.
 * Rights to modify the ACL are managed by an internal Guard implementation.
 */
contract ACLGuard is AuthChecker {

    /////
    // Constants:
    /////

    /**
     * @dev Permission for ALL actions is marked by 0xFF...FF
     */
    bytes32 constant public ALL = bytes32(uint(-1));

    /////
    // Storage:
    /////

    /**
     * @dev `acl` is the access control ledger. It is a multimap keyed by:
     * - `src`: the address of the *caller* that is checking if it canCall
     * - `dst`: the address of the *called* contract in which context the canCall is being checked
     * - `sig`: the function *signature* that `_src` wants to call in the context of `dst`
     */
    mapping (bytes32 => mapping (bytes32 => mapping (bytes32 => bool))) public acl;

    /////
    // Events:
    /////

    /**
     * @notice Emit when a permission is granted in the ACL.
     * @param src The address that is granted the permission.
     * @param dst The context (contract) in which the permission is granted.
     * @param sig The signature of the permission which is granted.
     */
    event LogPermit(
        address src,
        address dst,
        bytes4 sig
    );

    /**
     * @notice Emit when a permission is revoked in the ACL.
     * @param src The address that has the permission revoked.
     * @param dst The context (contract) in which the permission is granted.
     * @param sig The signature of the permission which is granted.
     */
    event LogForbid(
        address src,
        address dst,
        bytes4 sig
    );

    /////
    // Constructor:
    /////

    /**
     * @dev During deployment, the Guard contract must set it's own guard instance
     * to perform auth checks against.
     * @param _guard Adress of a guard implementation
     */
    // solhint-disable-next-line no-empty-blocks
    constructor(address _guard) public AuthChecker(_guard) {}

    /////
    // External functions:
    /////

    /**
     * @notice Grants permission to `src` to perform `sig` in the `dst` contract.
     * Requires authorization.
     * @param _src The address that is granted permission.
     * @param _dst The context (contract) in which the permission is granted.
     * @param _sig The signature of the permission that is granted.
     */
    function permit(address _src, address _dst, bytes4 _sig)
        external
        onlyAuthorized
    {
        _setPermit(bytes20(_src), bytes20(_dst), _sig);
    }

    /**
     * @notice Revokes permission from `src` to perform `sig` in the `dst` contract.
     * Requires authorization.
     * @param _src The address that has permission revoked.
     * @param _dst The context (contract) in which the permission is revoked.
     * @param _sig The signature of the permission that is revoked.
     */
    function forbid(address _src, address _dst, bytes4 _sig)
        external
        onlyAuthorized
    {
        _setForbid(bytes20(_src), bytes20(_dst), _sig);
    }

    /**
     * @notice Low level interface to grants permission to `src` to perform `sig` in the `dst`
     * contract. Requires authorization.
     * @param _src Bytes 32 value of the address that is granted permission.
     * @param _dst Bytes 32 value of the context (contract) in which the permission is granted.
     * @param _sig Bytes 32 value of the signature of the permission that is granted.
     */
    function permit(bytes32 _src, bytes32 _dst, bytes32 _sig)
        external
        onlyAuthorized
    {
        _setPermit(_src, _dst, _sig);
    }

    /**
     * @notice Low level interface to revoke permission from `src` to perform `sig` in the `dst`
     * contract. Requires authorization.
     * @param _src Bytes32 value of the address that has permission revoked.
     * @param _dst Bytes32 value of the context (contract) in which the permission is revoked.
     * @param _sig Bytes32 value of the signature of the permission that is revoked.
     */
    function forbid(bytes32 _src, bytes32 _dst, bytes32 _sig)
        external
        onlyAuthorized 
    {
        _setForbid(_src, _dst, _sig);
    }

    /////
    // Public view functions:
    /////

    /**
     * @notice Returns true if `src` can call the function with signture `sig` in the `dst`
     * contract, false otherwise.
     * @param _src The address that is being checked.
     * @param _dst The context (contract) in which the check is performed.
     * @param _sig The signature of the function which is being against.
     */
    function canCall(address _src, address _dst, bytes4 _sig)
        external
        view
        returns (bool)
    {
        return _isAuthorized(bytes20(_src), bytes20(_dst), _sig);
    }

    /////
    // Internal functions:
    /////

    /**
     * @dev Return true if `src` is authorized to perofm `sig` in the `dst` contract.
     * @param src The address that is being checked.
     * @param dst The context (contract) in which the check is performed.
     * @param sig The signature of the permission which is checked against.
     */
    function _isAuthorized(bytes32 src, bytes32 dst, bytes4 sig)
        internal
        view
        returns (bool)
    {
        return acl[src][dst][sig]
            || acl[src][dst][ALL]
            || acl[src][ALL][sig]
            || acl[src][ALL][ALL]
            || acl[ALL][dst][sig]
            || acl[ALL][dst][ALL]
            || acl[ALL][ALL][sig]
            || acl[ALL][ALL][ALL];
    }

    /**
      * @dev Internal function to grant permission to `src` to perform `sig` in the `dst` contract.
      * Requires authorization.
      * @param _src Bytes32 value of the address that is granted permission.
      * @param _dst Bytes32 value of the context (contract) in which the permission is granted.
      * @param _sig Bytes32 value of the signature of the permission that is granted.
      */
    function _setPermit(bytes32 _src, bytes32 _dst, bytes32 _sig)
        internal
    {
        acl[_src][_dst][_sig] = true;
        emit LogPermit(address(bytes20(_src)), address(bytes20(_dst)), bytes4(_sig));
    }

    /**
      * @dev Internal function to deny permission to `src` to perform `sig` in the `dst` contract.
      * Requires authorization.
      * @param _src Bytes32 value of the address that is granted permission.
      * @param _dst Bytes32 value of the context (contract) in which the permission is granted.
      * @param _sig Bytes32 value of the signature of the permission that is granted.
      */
    function _setForbid(bytes32 _src, bytes32 _dst, bytes32 _sig)
        internal
        onlyAuthorized
    {
        acl[_src][_dst][_sig] = false;
        emit LogForbid(address(bytes20(_src)), address(bytes20(_dst)), bytes4(_sig));
    }

}

