pragma solidity ^0.5.10;

import "../interfaces/IGuard.sol";

/**
 * @notice AuthChecker performs authorization checks on function calls. Authorization checks are
 * returned as the result of `canCall` of the configured Guard implementation.
 */
contract AuthChecker {

    /////
    // Storage:
    /////

    /**
     * @dev Authorization checks are performed against this guard contract instance.
     */
    IGuard public guard;

    /////
    // Events:
    /////

    /**
     * @notice Emit SetGuard when the Guard instance is changed.
     * @param setBy Address that set the new Guard instance.
     * @param guard Address of the new Guard instance.
     */
    event SetGuard(address indexed setBy, address indexed guard);

    /////
    // Modifiers:
    /////

    /**
     * @dev Modify the function so that it will revert if the caller is not auhorized to call the
     * function in the current context (this contract).
     */
    modifier onlyAuthorized {
        require(isAuthorized(msg.sender, msg.sig), "auth-checker-unauthorized");
        _;
    }

    /////
    // Constructor:
    /////

    /**
     * @dev Constructor should be provided with an address of a guard implementaion. If null, the
     * ACL denies all permissions to callers outside the contract.
     * @param _guard - An address of an IGuard implementation.
     */
    constructor(address _guard) public {
        _setGuard(_guard);
    }

    /////
    // External Functions:
    /////

    /**
     * @notice setGuard will set the new Guard instance. The caller must have permissions to call
     * this function in the current Guard instance.
     * @param _guard The address of the new Guard instance.
     */
    function setGuard(address _guard)
        public
        onlyAuthorized
    {
        _setGuard(_guard);
    }

    /////
    // External view Functions:
    /////

    /**
     * @notice isAuthorized will return `true` if `_src` address can call the function with the
     * `_sig` function selector in the current context (in this contract).
     * @dev The check performed by calling `canCall` on the Guard instance. If no Guard instance is
     * configured, function returns false (as a failsafe).
     * @param _src The address of the caller.
     * @param _sig The function selector of the function being called.
     * @return True, if the caller is authorized to make the call.
     */
    function isAuthorized(address _src, bytes4 _sig)
        public
        view
        returns (bool)
    {
        return _isAuthorized(_src, _sig);
    }

    /////
    // Internal functions:
    /////

    function _setGuard(address _guard)
        internal
    {
        guard = IGuard(_guard);
        emit SetGuard(msg.sender, _guard);
    }

    function _isAuthorized(address _src, bytes4 _sig)
        internal
        view
        returns (bool)
    {
        if (IGuard(0) == guard) {
            return false;  // If the guard is not set, do not authorize any other callers.
        }
        return guard.canCall(_src, address(this), _sig);
    }

}

