pragma solidity ^0.5.10;

import "../interfaces/IGuard.sol";

/**
 * @notice BaseGuard is a guard implementation that performs a basic check
 * for all permissions against a single allowed address.
 */
contract BaseGuard {

    /////
    // Storage:
    /////

    /**
     * @dev The address that is allowed to perform all calls.
     */
    address public allowed;

    /////
    // Events:
    /////

    /**
     * @dev Emit SetAllowed when the allowed address is changed.
     * @param setBy The address that has changed the allowed address.
     * @param newAllowed The new allowed address
     */
    event SetAllowed(address indexed setBy, address indexed newAllowed);

    /////
    // Modifiers:
    /////

    /**
     * @notice onlyAllowed modifies the function so that it is required
     * to be called by the `allowed` address.
     */
    modifier onlyAllowed() {
        require(msg.sender == allowed, "base-guard-not-allowed");
        _;
    }

    /////
    // Constructor:
    /////

    /**
     * @dev During construction, msg.sender becomes `allowed`.
     */
    constructor(address _allowed) public {
        _setAllowed(_allowed);
    }

    /////
    // External functions:
    /////

    /**
     * @notice Changes the allowed addres to `_allowed`. Must be called by current `_allowed`
     * address.
     * @param _allowed The new address to be allowed permissions.
     */
    function setAllowed(address _allowed)
        external
        onlyAllowed
    {
        _setAllowed(_allowed);
    }

    /////
    // External view functions:
    /////

    /**
     * @notice Returns true only if _src is the boss of this contract
     * @param _src The address that is being checked.
     */
    function canCall(address _src, address, bytes4)
        external
        view
        returns (bool)
    {
        return _isAllowed(_src);
    }

    /////
    // Internal functions:
    /////

    function _setAllowed(address _allowed)
        internal
    {
        allowed = _allowed;
        emit SetAllowed(msg.sender, _allowed);
    }

    function _isAllowed(address _adr)
        internal
        view
        returns (bool)
    {
        return _adr == allowed;
    }
}

