pragma solidity ^0.5.10;

import "../permissions/AuthChecker.sol";

/**
* @title BaseRegistry
* @author Disberse LTD
* @notice Provide an extended ERC-780 claims registry.
* A special authorized addresses is able to set claims from the zero address.
* @dev BaseRegisty is the abstract implementation of the Registry interface.
*
*/
contract BaseRegistry is AuthChecker {

    /////
    // Constants:
    /////

    /**
     * @dev The address from which the authority can make claims.
     */
    address public constant AUTH_CLAIM_ADDR = address(0);

    /////
    // Constructor:
    /////

    /**
     * @dev Constructor takes the Guard implementation address and passes it to the base AuthChecker constuctor.
     * @param _guard The address of the guard implementation.
     */
    // solhint-disable-next-line no-empty-blocks
    constructor(address _guard) public AuthChecker(_guard) {}

    /////
    // State data:
    /////

    /**
     * @dev Mapping:
     *  Issuer => Subject => Claim key => Claim value
     */
    mapping(address => mapping(address => mapping(bytes32 => bytes32))) public registry;

    /////
    // Public interface:
    /////

    /**
    * @notice Get an exisiting claim from the registry.
    * @param _issuer Address that issued the claim
    * @param _subject Address of the claim recipient (who the claim is about)
    * @param _key Claim key (what the claim is about)
    * @return The value of the claim found, or null
    */
    function getClaim(address _issuer, address _subject, bytes32 _key) external view
        returns(bytes32)
    {
        return _getClaim(_issuer, _subject, _key);
    }

    /**
    * @notice Set a new claim in the registry.
    * @param _subject Address of the claim recipient (who the claim is about)
    * @param _key Claim key (what the claim is about)
    * @param _value Claim value (What is being claimed)
    */
    function setClaim(address _subject, bytes32 _key, bytes32 _value) external {
        _setClaim(_subject, _key, _value);
    }

        /**
    * @notice Set a claim with the caller as the `subject` of the claim.
    * @param _key Claim key (what the claim is about)
    * @param _value Claim value (What is being claimed)
    */
    function setSelfClaim(bytes32 _key, bytes32 _value) external {
        _setSelfClaim(_key, _value);
    }

    /**
    * @notice Remove an existing claim from the registry. Claims can only be removed by the `issuer` that has set them.
    * @param _issuer Address that issued the claim
    * @param _subject Address of the claim recipient (who the claim is about)
    * @param _key Claim key (what the claim is about)
    */
    function removeClaim(address _issuer, address _subject, bytes32 _key) external {
        _removeClaim(_issuer, _subject, _key);
    }

    /**
    * @notice Sets a claim from the special AUTH_CLAIM_ADDR. This action requires authorization.
    * @param _subject Address of the claim recipient (who the claim is about)
    * @param _key Claim key (what the claim is about)
    * @param _value Claim value (What is being claimed)
    */
    function setAuthClaim(address _subject, bytes32 _key, bytes32 _value) external {
        _setAuthClaim(_subject, _key, _value);
    }

    /**
    * @notice Removes a claim from the special AUTH_CLAIM_ADDR. This action requires authorization.
    * @param _subject Address of the claim recipient (who the claim is about)
    * @param _key Claim key (what the claim is about)
    */
    function removeAuthClaim(address _subject, bytes32 _key) external {
        _removeAuthClaim(_subject, _key);
    }

    /////
    // Internal functions:
    /////

    /**
    * @notice Get an exisiting claim from the registry.
    * @param _issuer Address that issued the claim
    * @param _subject Address of the claim recipient (who the claim is about)
    * @param _key Claim key (what the claim is about)
    * @return The value of the claim found, or null
    */
    function _getClaim(address _issuer, address _subject, bytes32 _key)
        internal
        view
        returns(bytes32)
    {
        return registry[_issuer][_subject][_key];
    }

    /////
    // Function stubs:
    /////

    function _setClaim(address, bytes32, bytes32) internal;
    function _setSelfClaim(bytes32, bytes32) internal;
    function _removeClaim(address, address, bytes32) internal;
    function _setAuthClaim(address, bytes32, bytes32) internal;
    function _removeAuthClaim(address, bytes32) internal;

}

