pragma solidity ^0.5.10;

import "../permissions/AuthChecker.sol";

import "./Bootstrapper.sol";

/**
* @title Factory
* @author Disberse LTD
* @notice A CREATE2 contract factory. Specifically intended to deploy contracts
* with indeterminate init code. Deployment pattern based on ideas from
* https://blog.ricmoo.com/wisps-the-magical-world-of-create2-5c2177027604
*/
contract Factory is AuthChecker {

    /////
    // State data
    /////

    // the address that contains the runtimeCode. The initCode should call back
    // into this contract with findBootstrapper(), then call bootstrap() on
    // the returned address to get the runtime code that should be deployed
    // at the create2 address
    address public bootstrapperAddr;

    // the init code to deploy
    bytes public initCode;

    /////
    // Events
    /////

    event Deploy(
        address indexed src,    // sender of the deploy tx
        address indexed dst,    // new contract address
        bytes32 indexed salt    // salt used to deploy contract
    );

    /////
    // Constructor
    /////

    /**
    * @dev Constructor
    * @param _bootstrapperAddr Address of the bootstrapper contract that holds
    * the desired runtime code for deployed contracts
    * @param _initCode The code to deploy
    */
    constructor(
        address _guard,
        address _bootstrapperAddr,
        bytes memory _initCode
    )
        public
        AuthChecker(_guard)
    {
        bootstrapperAddr = _bootstrapperAddr;
        initCode = _initCode;
    }

    /**
    * @dev Deploys a contract with CREATE2 using initCode and the supplied salt.
    * Can only be called by authorized entities
    * @param _salt The salt that determines the deployed contract address
    * @return The address of the deployed contract
    */
    function deploy(bytes32 _salt)
        external
        onlyAuthorized
        returns (address payable dst)
    {
        // copy initcode into memory
        bytes memory _initCode = initCode;
        bytes32 deployedCodeHash;

        // deploy contract via create2
        assembly {
            // create2(wei to send, initcode offset, initcode length, salt)
            // init code data starts 32 (0x20) bytes after the _initCode
            // pointer. Bytes [0, 31] after the pointer contains the length of
            // the init code data,
            dst := create2(0, add(_initCode, 0x20), mload(_initCode), _salt)

            // check that code is deployed at the address (create2 succeeded)
            if iszero(extcodesize(dst)) {
                revert(0, 0)
            }

            // get the hash of the newly deployed code
            deployedCodeHash := extcodehash(dst)
        }

        // confirm that the correct runtime code is deployed
        bytes32 bootstrapCodeHash = keccak256(
            Bootstrapper(bootstrapperAddr).bootstrap()
        );
        require(deployedCodeHash == bootstrapCodeHash, "factory-deployment-failed");

        emit Deploy(msg.sender, dst, _salt);
    }

    /**
    * @dev Changes the bootstrapper address. Only authorized addresses can call.
    * @param _bootstrapperAddr The new bootstrapper address
    */
    function setBootstrapper(address _bootstrapperAddr)
        external
        onlyAuthorized
    {
        bootstrapperAddr = _bootstrapperAddr;
    }

    /**
    * @dev Returns the bootstraper address. This function is redundant, but it's
    * function signature is hardocded into the deployer bytecode, and it is
    * kept to avoid bugs should the name of the bootstrapperAddr change.
    * @return The address of the bootstrapper contract
    */
    function findBootstrapper()
        public
        view
        returns (address)
    {
        return bootstrapperAddr;
    }

}

