pragma solidity ^0.5.10;

import "../libraries/SafeMath.sol";

/**
* @title Grave
* @author Disberse LTD
* @notice Allows addresses to deposit their funds to be claimed at a later time. Used to safely hold
* ether released from a self-destructed contract.
*/
contract Grave {

    using SafeMath for uint256;

    // Mapping: owner => balance.
    mapping (address => uint256) public balances;

    /////
    // Fallback function:
    /////

    /**
     * @dev Fallback function. Adds `msg.value` to the sender's ether balance.
    */
    function ()
        external
        payable
    {
        balances[msg.sender] = balances[msg.sender].add(msg.value);
    }

    /////
    // External functions:
    /////

    /**
     * @notice Withdraw all funds from the contract. Funds cannot be incrementally withdrawn.
    */
    function withdraw()
        external
    {
        uint256 balance = balances[msg.sender];
        balances[msg.sender] = 0;
        msg.sender.transfer(balance);
    }

}

