pragma solidity ^0.5.10;

import "./AuthChecker.sol";

contract Guard is AuthChecker {

    /////
    // Constants:
    /////

    /**
     * @dev Permission for ALL actions is marked by 0xFF...FF
     */
    bytes32 constant public ALL = bytes32(uint(-1));

    /////
    // Contract storage:
    /////

    /**
     * @dev `acl` is the access control ledger. It is a multimap keyed by:
     * - `src`: the address of the *caller* that is checking if it canCall
     * - `dst`: the address of the *called* contract in which context the canCall is being checked
     * - `sig`: the function *signature* that `_src` wants to call in the context of `dst`
     */
    mapping (bytes32 => mapping (bytes32 => mapping (bytes32 => bool))) internal acl;

    /////
    // Events:
    /////

    /**
     * @notice Emit when a permission is granted in the ACL.
     * @param src The address that is granted the permission.
     * @param dst The context (contract) in which the permission is granted.
     * @param sig The signature of the permission which is granted.
     */
    event LogPermit(
        bytes32 src,
        bytes32 dst,
        bytes32 sig
    );

    /**
     * @notice Emit when a permission is revoked in the ACL.
     * @param src The address that has the permission revoked.
     * @param dst The context (contract) in which the permission is granted.
     * @param sig The signature of the permission which is granted.
     */
    event LogForbid(
        bytes32 src,
        bytes32 dst,
        bytes32 sig
    );

    /////
    // Internal functions:
    /////

    /**
     * @notice Returns true if `src` is authorized to perofm `sig` in the `dst` contract, false otherwise.
     * @param src The address that is being checked.
     * @param dst The context (contract) in which the check is performed.
     * @param sig The signature of the permission which is checked against.
     */
    function _isAuthorized(bytes32 src, bytes32 dst, bytes32 sig)
        public
        view
        returns (bool)
    {
        return acl[src][dst][sig]
            || acl[src][dst][ALL]
            || acl[src][ALL][sig]
            || acl[src][ALL][ALL]
            || acl[ALL][dst][sig]
            || acl[ALL][dst][ALL]
            || acl[ALL][ALL][sig]
            || acl[ALL][ALL][ALL];
    }

    /**
     * @notice Grants permission to `src` to perform `sig` in the `dst` contract.
     * @param _src The address that is granted permission.
     * @param _dst The context (contract) in which the permission is granted.
     * @param _sig The signature of the permission that is granted.
     */
    function _permit(bytes32 _src, bytes32 _dst, bytes32 _sig)
        internal
        onlyAuthorized
    {
        acl[_src][_dst][_sig] = true;
        emit LogPermit(_src, _dst, _sig);
    }

    /**
     * @notice Revokes permission from `src` to perform `sig` in the `dst` contract.
     * @param _src The address that has permission revoked.
     * @param _dst The context (contract) in which the permission is revoked.
     * @param _sig The signature of the permission that is revoked.
     */
    function _forbid(bytes32 _src, bytes32 _dst, bytes32 _sig)
        internal
        onlyAuthorized
    {
        acl[_src][_dst][_sig] = false;
        emit LogForbid(_src, _dst, _sig);
    }

    /////
    // Public functions:
    /////

    /**
     * @notice Grants permission to `src` to perform `sig` in the `dst` contract.
     * @param _src The address that is granted permission.
     * @param _dst The context (contract) in which the permission is granted.
     * @param _sig The signature of the permission that is granted.
     */
    function permit(bytes32 _src, bytes32 _dst, bytes32 _sig)
        public
    {
        _permit(_src, _dst, _sig);
    }

    /**
     * @notice Revokes permission from `src` to perform `sig` in the `dst` contract.
     * @param _src The address that has permission revoked.
     * @param _dst The context (contract) in which the permission is revoked.
     * @param _sig The signature of the permission that is revoked.
     */
    function forbid(bytes32 _src, bytes32 _dst, bytes32 _sig)
        public
    {
        _forbid(_src, _dst, _sig);
    }

    /**
     * @notice Grants permission to `src` to perform `sig` in the `dst` contract.
     * @param _src The address that is granted permission.
     * @param _dst The context (contract) in which the permission is granted.
     * @param _sig The signature of the permission that is granted.
     */
    function permit(address _src, address _dst, bytes32 _sig)
        public
    {
        _permit(bytes20(_src), bytes20(_dst), _sig);
    }

    /**
     * @notice Revokes permission from `src` to perform `sig` in the `dst` contract.
     * @param _src The address that has permission revoked.
     * @param _dst The context (contract) in which the permission is revoked.
     * @param _sig The signature of the permission that is revoked.
     */
    function forbid(address _src, address _dst, bytes32 _sig)
        public
    {
        _forbid(bytes20(_src), bytes20(_dst), _sig);
    }

    /////
    // Public view functions:
    /////

    /**
     * @notice Returns true if `src` can call the function with signture `sig` in the `dst` contract, false otherwise.
     * @param _src The address that is being checked.
     * @param _dst The context (contract) in which the check is performed.
     * @param _sig The signature of the function which is being against.
     */
    function canCall(address _src, address _dst, bytes32 _sig)
        public
        view
        returns (bool)
    {
        return _isAuthorized(bytes20(_src), bytes20(_dst), _sig);
    }
}

