pragma solidity ^0.5.10;

import "../permissions/AuthChecker.sol";

contract Killable is AuthChecker {

    /////
    // State data
    /////

    // the address to self-destruct to
    address payable public grave;

    /////
    // Events
    /////

    event SetGrave(address indexed grave);
    event Kill(address indexed recipient);

    /////
    // Constructor
    /////

    /**
    * @dev Constructor
    * @param _guard Address of a guard implementation
    */
    // solhint-disable-next-line no-empty-blocks
    constructor(address _guard) public AuthChecker(_guard) {}

    /////
    // Externally authorized functions
    /////

    function setGrave(address payable _grave) public onlyAuthorized {
        _setGrave(_grave);
    }

    function kill() public onlyAuthorized {
        emit Kill(grave);
        selfdestruct(grave);
    }

    /////
    // Internal functions
    /////

    function _setGrave(address payable _grave) internal {
        grave = _grave;
        emit SetGrave(_grave);
    }
}
