pragma solidity ^0.5.10;

import "./Registry.sol";

/**
 * @title ReasonLoggingRegistry
 * @author Disberse LTD
 * @notice ReasonLoggingRegistry logs all changes to the registry with a hash of a reason of change.
 */
contract ReasonLoggingRegistry is Registry {

    /////
    // Events:
    /////

    /**
     * @dev Emit this event when a claim is set on the registry.
     * - issuer The address that issued this claim
     * - subject Address of the claim recipient (who the claim is about)
     * - key Claim key (what the claim is about)
     * - value Claim value (what is being claimed)
     * - reason Hash of the off chain stored reason information
     */
    event ClaimSetReason(
        address issuer,
        address subject,
        bytes32 key,
        bytes32 value,
        bytes32 reason
    );

    /**
     * @dev Emit this event when a claim is removed from the registry.
     * - issuer The address that has issued this claim
     * - subject Address of the claim recipient (who the claim is about)
     * - key Claim key (what the claim is about)
     * - value Claim value (what is being claimed)
     * - reason Hash of the off chain stored reason information
     */
    event ClaimRemovedReason(
        address issuer,
        address subject,
        bytes32 key,
        bytes32 reason
    );

    /**
     * @dev Emit this event when an authorized claim is set on the registry.
     * - issuer The address of the administratior making the claim
     * - subject Address of the claim recipient (who the claim is about)
     * - key Claim key (what the claim is about)
     * - value Claim value (what is being claimed)
     * - reason Hash of the off chain stored reason information
     */
    event AuthClaimSetReason(
        address issuer,
        address subject,
        bytes32 key,
        bytes32 value,
        bytes32 reason
    );

    /**
     * @dev Emit this event when an authorized claim is removed from the registry.
     * - issuer The address of the administrator removing the claim
     * - subject Address of the claim recipient (who the claim is about)
     * - key Claim key (what the claim is about)
     * - value Claim value (what is being claimed)
     * - reason Hash of the off chain stored reason information
     */
    event AuthClaimRemovedReason(
        address issuer,
        address subject,
        bytes32 key,
        bytes32 reason
    );

    /////
    // Constructor:
    /////

    /**
     * @dev Constructor takes the Guard implementation address and passes it to the base constuctor.
     * @param _guard The address of the guard implementation.
     */
    // solhint-disable-next-line no-empty-blocks
    constructor(address _guard) public Registry(_guard) {}

    /////
    // External functions:
    /////

    /**
     * @notice Set a new claim in the registry.
     * @param _subject Address of the claim recipient (who the claim is about)
     * @param _key Claim key (what the claim is about)
     * @param _value Claim value (what is being claimed)
     * @param _reason Hash of the reason the claim was set
    */
    function setClaim(
        address _subject,
        bytes32 _key,
        bytes32 _value,
        bytes32 _reason
    )
        external
    {
        super._setClaim(_subject, _key, _value);
        emit ClaimSetReason(msg.sender, _subject, _key, _value, _reason);
    }

    /**
     * @notice Set a claim with the caller as the `subject` of the claim.
     * @param _key Claim key (what the claim is about)
     * @param _value Claim value (what is being claimed)
     * @param _reason Hash of the reason the claim was set
    */
    function setSelfClaim(
        bytes32 _key,
        bytes32 _value,
        bytes32 _reason
    )
        external
    {
        super._setSelfClaim(_key, _value);
        emit ClaimSetReason(msg.sender, msg.sender, _key, _value, _reason);
    }

    /**
     * @notice Remove an existing claim from the registry. Claims can only be removed by the 
     * `issuer` that has previously set them.
     * @param _issuer Address that issued the claim
     * @param _subject Address of the claim recipient (who the claim is about)
     * @param _key Claim key (what the claim is about)
     * @param _reason Hash of the reason the claim was removed
    */
    function removeClaim(
        address _issuer,
        address _subject,
        bytes32 _key,
        bytes32 _reason
    )
        external
    {
        super._removeClaim(_issuer, _subject, _key);
        emit ClaimRemovedReason(_issuer, _subject, _key, _reason);
    }

    /**
     * @notice Sets a claim from the special AUTH_CLAIM_ADDR. This action requires authorization.
     * @param _subject Address of the claim recipient (who the claim is about)
     * @param _key Claim key (what the claim is about)
     * @param _value Claim value (What is being claimed)
     * @param _reason Hash of the reason the claim was set
    */
    function setAuthClaim(
        address _subject,
        bytes32 _key,
        bytes32 _value,
        bytes32 _reason
    )
        external
    {
        super._setAuthClaim(_subject, _key, _value);
        emit AuthClaimSetReason(msg.sender, _subject, _key, _value, _reason);
    }

    /**
     * @notice Removes a claim from the special AUTH_CLAIM_ADDR. This action requires authorization.
     * @param _subject Address of the claim recipient (who the claim is about)
     * @param _key Claim key (what the claim is about)
     * @param _reason Hash of the reason the claim was removed
    */
    function removeAuthClaim(
        address _subject,
        bytes32 _key,
        bytes32 _reason
    )
        external
    {
        super._removeAuthClaim(_subject, _key);
        emit AuthClaimRemovedReason(msg.sender, _subject, _key, _reason);
    }

}

