pragma solidity ^0.5.10;

import "./BaseRegistry.sol";

/**
* @title Registry
* @author Disberse LTD
* @notice Provide an extended ERC-780 claims registry.
* A special authorized addresses is able to set claims from the zero address.
*/
contract Registry is BaseRegistry {

    /////
    // Constants:
    /////

    /**
     * @dev The address from which the authority can make claims.
     */
    address public constant AUTH_CLAIM_ADDR = address(0);

    /////
    // Modifiers:
    /////

    /**
     * @dev Modify the function to revert if not called by the given `issuer`.
     */
    modifier onlyIssuer(address _issuer) {
        require(msg.sender == _issuer, "registry-not-issuer");
        _;
    }

    /////
    // Constructor:
    /////

    /**
     * @dev Constructor takes the Guard implementation address and passes it to the BaseRegistry constuctor.
     * @param _guard The address of the guard implementation.
     */
    // solhint-disable-next-line no-empty-blocks
    constructor(address _guard) public BaseRegistry(_guard) {}

    /////
    // Internal functions:
    /////

    /**
    * @notice Set a new claim in the registry.
    * @param _subject Address of the claim recipient (who the claim is about)
    * @param _key Claim key (what the claim is about)
    * @param _value Claim value (What is being claimed)
    */
    function _setClaim(
        address _subject,
        bytes32 _key,
        bytes32 _value
    )
        internal
    {
        registry[msg.sender][_subject][_key] = _value;
    }

    /**
    * @notice Set a claim with the caller as the `subject` of the claim.
    * @param _key Claim key (what the claim is about)
    * @param _value Claim value (What is being claimed)
    */
    function _setSelfClaim(
        bytes32 _key,
        bytes32 _value
    )
        internal
    {
        registry[msg.sender][msg.sender][_key] = _value;
    }

    /**
    * @notice Remove an existing claim from the registry. Claims can only be removed by the `issuer` that has set them.
    * @param _issuer Address that issued the claim
    * @param _subject Address of the claim recipient (who the claim is about)
    * @param _key Claim key (what the claim is about)
    */
    function _removeClaim(
        address _issuer,
        address _subject,
        bytes32 _key
    )
        internal
        onlyIssuer(_issuer)
    {
        delete registry[_issuer][_subject][_key];
    }

    /**
    * @notice Sets a claim from the special AUTH_CLAIM_ADDR. This action requires authorization.
    * @param _subject Address of the claim recipient (who the claim is about)
    * @param _key Claim key (what the claim is about)
    * @param _value Claim value (What is being claimed)
    */
    function _setAuthClaim(
        address _subject,
        bytes32 _key,
        bytes32 _value
    )
        internal
        onlyAuthorized
    {
        registry[AUTH_CLAIM_ADDR][_subject][_key] = _value;
    }

    /**
    * @notice Removes a claim from the special AUTH_CLAIM_ADDR. This action requires authorization.
    * @param _subject Address of the claim recipient (who the claim is about)
    * @param _key Claim key (what the claim is about)
    */
    function _removeAuthClaim(
        address _subject,
        bytes32 _key
    )
        internal
        onlyAuthorized
    {
        delete registry[AUTH_CLAIM_ADDR][_subject][_key];
    }

}

