pragma solidity ^0.5.10;

import "../interfaces/ISigValidator.sol";
import "../libraries/SafeMath.sol";

library SigLib {

    using SafeMath for uint256;

    // keccak256("isValidSignature(bytes32, bytes)")
    bytes4 internal constant VALIDATOR_MAGIC_VALUE = bytes4(0);

    function parseSig(bytes memory _sigs, uint _pos)
        internal
        pure
        returns (uint8 v, bytes32 r, bytes32 s)
    {
        // The signature format is a compact form of:
        // {bytes32 r}{bytes32 s}{uint8 v}  (uint8 is not padded)
        assembly {
            // the constant part of each sig is 65 (0x41) bytes long, and the
            // sigs start 32 (0x20) bytes after the _sigs pointer. So, the
            // start of a sig is `_sigs + 32 bytes + (65 bytes * _pos)`
            // where _pos is the index of the sig
            let sigPtr := add(add(_sigs, 0x20), mul(_pos, 0x41))

            // r is the first 32 bytes of the constant part of the sig
            r := mload(sigPtr)

            // s is bytes [32, 63] of the constant part of the sig
            s := mload(add(sigPtr, 0x20))

            // byte(n, x) => nth byte of x, where 0th byte is most significant
            // v is the last byte of the sig - 65th byte, or the 0th byte of
            // bytes [64, 95]
            v := byte(0, mload(add(sigPtr, 0x40)))
        }
    }

    function parseSigV(bytes memory _sigs, uint _pos)
        internal
        pure
        returns (uint8 v)
    {
        // {bytes32 r}{bytes32 s}{uint8 v}
        assembly {
            // the constant part of each sig is 65 (0x41) bytes long, and the
            // sigs start 32 (0x20) bytes after the _sigs pointer. So, the
            // start of a sig is `_sigs + 32 bytes + (65 bytes * _pos)`
            // where _pos is the index of the sig
            let sigPtr := add(add(_sigs, 0x20), mul(_pos, 0x41))

            // byte(n, x) => nth byte of x, where most significant byte is the 0th byte
            // v is the last byte of the sig (65th byte)
            v := byte(0, mload(add(sigPtr, 0x40)))
        }
    }

    function parseSigR(bytes memory _sigs, uint _pos)
        internal
        pure
        returns (bytes32 r)
    {
        // {bytes32 r}{bytes32 s}{uint8 v}
        assembly {
            // the constant part of each sig is 65 (0x41) bytes long, and the
            // sigs start 32 (0x20) bytes after the _sigs pointer. So, the
            // start of a sig is `_sigs + 32 bytes + (65 bytes * _pos)`
            // where _pos is the index of the sig (e.g. the first sig is
            let sigPtr := add(add(_sigs, 0x20), mul(_pos, 0x41))

            // r is the first 32 bytes of the constant part of the sig
            r := mload(sigPtr)
        }
    }

    // _offset is the offset relative to the *data part* of
    // the signature (i.e. not including the size)
    function parseContractSig(bytes memory _sigs, bytes32 _s)
        internal
        pure
        returns (uint size, bytes memory ptr)
    {
        assembly {
            // for a contract sig, the s from the constant part of the sig is
            // an offset from the start of the sig data (_sigs + 32 bytes)
            // that indicates the start of the contract sig data.
            ptr := add(add(_sigs, 0x20), _s)

            // The contract sig data is stored as a standard tighly packed
            // bytes array (i.e. bytes not byte[]), where the first 32 bytes
            // is the length of the bytes array
            size := mload(ptr)
        }
    }

    function recoverEoaSig(bytes memory _sigs, bytes32 _hash, uint _pos)
        internal
        pure
        returns (address)
    {
        // for a standard EOA sig, we just get v, r, s and use ecrecover
        (uint8 v, bytes32 r, bytes32 s) = parseSig(_sigs, _pos);
        return ecrecover(_hash, v, r, s);
    }

    function recoverSignedHashSig(bytes memory _sigs, uint _pos)
        internal
        pure
        returns (address)
    {
        // for a signed hash sig, the signer is encoded in r. We return this
        // address, but still need to check that address has signed the hash
        bytes32 r = parseSigR(_sigs, _pos);
        return address(uint256(r));
    }

    function recoverContractSig(
        bytes memory _sigs,
        bytes32 _hash,
        uint _pos,
        uint _minSigCount
    )
        internal
        view
        returns (address)
    {
        (, bytes32 r, bytes32 s) = parseSig(_sigs, _pos);

        // the address of the contract is encoded in r
        address signer = address(uint256(r));

        // check that s is not pointing inside static part of the signatures
        // this does not check if > the expected number of signatures are sent
        require(uint256(s) >= _minSigCount.mul(65), "invalid-contract-sig-s");

        // check that s is in-bounds
        require(uint256(s).add(32) <= _sigs.length, "invalid-contract-sig-s");

        // get the contract signature, where s is the offset of the
        // contract sig relative to the data part of the signature
        // (i.e. the length of _sigs not included)
        (uint contractSigLen, bytes memory contractSig) = parseContractSig(_sigs, s);

        // check that the contract signature is in-bounds. **Not safe
        // to use contractSig until contractSigLen has been checked
        require(
            uint(s).add(32).add(contractSigLen) <= _sigs.length,
            "invalid-contract-sig"
        );

        // check signature
        bytes4 validatorResponse = ISigValidator(signer).isValidSignature(_hash, contractSig);
        require(
            validatorResponse == VALIDATOR_MAGIC_VALUE,
            "invalid-contract-sig"
        );

        return signer;
    }

    function encodeEip712Data(bytes32 _domainSeparator, bytes32 _dataHash)
        internal
        pure
        returns (bytes memory)
    {
        return abi.encodePacked(
            byte(0x19), byte(0x01), _domainSeparator, _dataHash
        );
    }

}

